import React, { useState, useEffect } from "react";
import axios from 'axios';
import { find } from "lodash";
import { useHistory } from "react-router-dom";
import ProfileDetails from "./ProfileDetails";

function Profile() {
	const [allUsers, setAllUsers] = useState(null);
	const [user, setUser] = useState({});
	const [open, setOpen] = useState(false);

	const history = useHistory();

	const apiURL = "https://panorbit.in/api/users.json";

	const fetchProfileDetails = async () => {
		let response = await axios.get(apiURL);
		setAllUsers(response.data.users);

		if (response) {
			let id = history.location.state.user_id;

			let user = find(response.data.users, ['id', id]);

	    setUser(user);
		}

	}

	useEffect(() => {
		fetchProfileDetails();
	}, []);

	return (
		<div className="flex overflow-y-auto bg-white p-6" style={{height: "50rem"}}>
		  <div className="hidden bg-indigo-700 rounded-3xl md:flex md:flex-shrink-0">
		    <div className="flex flex-col w-64">
		      <div className="flex flex-col flex-grow pt-5 pb-4 overflow-y-auto">
		        <div className="flex-1 flex flex-col">
		          <nav className="flex-1 px-6 space-y-1 my-56">
		            <a href="#" className="group flex items-center px-2 py-4 text-sm font-medium text-white">
		              Profile
		            </a>

		            <a href="#" className="group flex items-center px-2 py-4 text-sm font-medium text-gray-500 border-t border-b border-gray-500 focus:text-white">
		              Posts
		            </a>

		            <a href="#" className="group flex items-center px-2 py-4 text-sm font-medium text-gray-500 border-b border-gray-500 focus:text-white">
		              Gallery
		            </a>

		            <a href="#" className="group flex items-center px-2 py-4 text-sm font-medium text-gray-500 focus:text-white">
		              ToDo
		            </a>
		          </nav>
		        </div>
		      </div>
		    </div>
		  </div>
		  <div className="flex flex-col w-0 flex-1 overflow-hidden bg-white p-8">
		    <div className="relative z-10 flex-shrink-0 flex h-16 bg-white border-b pb-5">
		      <button className="px-4 border-r border-gray-200 text-gray-500 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500 md:hidden">
		        <span className="sr-only">Open sidebar</span>
		        <svg className="h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true">
		          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h7" />
		        </svg>
		      </button>
		      <div className="flex-1 px-4 flex justify-between items-center">
          	<div className="text-xl font-medium leading-6 text-gray-700">Profile</div>
		        <div className="ml-4 flex items-center md:ml-6">
		          <div className="ml-3 relative">
		            <div>
		              <button className="max-w-xs bg-white flex items-center text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" id="user-menu" aria-haspopup="true" onClick={() => setOpen(!open)}>
		                <span className="sr-only">Open user menu</span>
		                <img className="h-8 w-8 rounded-full" src={user && user.profilepicture} alt="" />
		              	<div className="ml-3 text-base text-gray-700">{user && user.name}</div>
		              </button>
		            </div>

		          {/** open pop up when clicked on username **/
		          	open && (
			            <div className="origin-top-right absolute right-0 mt-2 w-64 rounded-3xl shadow-2xl bg-white ring-1 ring-black ring-opacity-5 px-4 py-12" role="menu" aria-orientation="vertical" aria-labelledby="user-menu">
			            	<div className="border-b">
				            	<div className="flex justify-center">
				            		<img className="h-20 w-20 rounded-full" src={user && user.profilepicture} alt="" />
			            		</div>
			            		<div className="ml-3 text-center text-base text-gray-700 pt-2">{user && user.name}</div>
			            		<div className="ml-3 text-center text-sm text-gray-500 pb-2">{user && user.email}</div>
		            		</div>

		            		<div className="h-20 overflow-y-auto">
		            			{allUsers && allUsers.map((user, index) => {
		            				return (
		            					<div className="flex justify-center border-b py-2">
		            						<img className="h-8 w-8 rounded-full" src={user.profilepicture} alt="" />
		            						<div className="ml-3 text-center text-base text-gray-700">{user && user.name}</div>
		            					</div>
	            					)
		            			})}
	            			</div>

	            			<div className="flex justify-center py-4">
	            				<button className="max-w-xs bg-red-700 px-3 py-2 text-white flex items-center text-sm rounded-full focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500" id="user-menu" aria-haspopup="true" 
	            					onClick={() => {
	            						setOpen(!open);
	            						history.push("/")
	            					}}
            					>
				                Sign out
				              </button>
	            			</div>
			            </div>
	          		)
		          }
		          </div>
		        </div>
		      </div>
		    </div>

		    <main className="flex-1 relative focus:outline-none bg-white" tabIndex="0">
			    {user && (
			      <ProfileDetails user={user} />
		    	)}
		    </main>
		  </div>
		</div>
	)
}

export default Profile;
