import React, { useState, useEffect } from "react";
import axios from 'axios';

function LandingPage({}) {
	const [users, setUsers] = useState(null);

	const apiURL = "https://panorbit.in/api/users.json";

	useEffect(() => {
		fetchUsers();
	}, [])

	const fetchUsers = async () => {
		let response = await axios.get(apiURL);

    setUsers(response.data.users);
    fetchUsers();
	}

	console.log("users", users);

	return (
		<div className="bg-gradient-to-br from-blue-400 to-white-500 h-screen">
     	<div className="relative bg-gray-50 pt-16 pb-20 px-4 sm:px-6 lg:pt-24 lg:pb-28 lg:px-8">
  			<div className="absolute inset-0">
    			<div className="bg-white h-1/3 sm:h-2/3"></div>
  			</div>
  			<div className="mt-12 max-w-xs mx-auto">
      		<div className="flex flex-col rounded-2xl shadow-lg overflow-hidden">
        		<div className="flex-shrink-0">
        			<div className="">
						    <h2 className="text-xl tracking-tight font-extrabold text-gray-900 p-6 text-center bg-gray-200">
					        Select an account
					      </h2>

					      <div className="bg-white text-center">
					      	{users.map((user, index) => {
					      		return	(
						      		<div>{user.name}</div>
				      			)
					      	})}
					      </div>
					    </div>
        		</div>
        	</div>
      	</div>
  		</div>
		</div>
	)
}

export default LandingPage;
